function ViewStimulifigure;subplot(2,3,1);for i=1:10    S = OnSpot( 50 );    colormap('gray');    pcolor( S );    axis('square');    title('ON Spot', 'FontSize', 24.0 );        pause(0.2);endsubplot(2,3,2);for i=1:10    S = OffSpot( 50 );    colormap('gray');    pcolor( S );    axis('square');    title('OFF Spot', 'FontSize', 24.0 );        pause(0.2);end subplot(2,3,3);theta = rand * pi;for i=1:10    S = OnBar( 50, theta );    colormap('gray');    pcolor( S );    axis('square');    title('ON Bar', 'FontSize', 24.0 );        pause(0.2);endsubplot(2,3,4);theta = rand * pi;for i=1:10    S = OffBar( 50, theta );    colormap('gray');    pcolor( S );    axis('square');    title('OFF Bar', 'FontSize', 24.0 );        pause(0.2);endsubplot(2,3,5);for i=1:10    [S] = WhiteNoise( 50 );    colormap('gray');    pcolor( S );    axis('square');    title('White Noise', 'FontSize', 24.0 );        pause(0.2);end